INSERT INTO movie (title, overview, release_date, poster_path) VALUES ('Jurassic World: Dominion', 'The third installment of the new "Jurassic World" trilogy. Plot details are currently unknown.', '2022-06-10', 'https://image.tmdb.org/t/p/original//wz3MRiMmoz4qX6fWz7z5dSqgEBH.jpg');
INSERT INTO movie (title, overview, release_date, poster_path) VALUES ('Spider-Man: No Way Home', 'The third Spider-Man film in the Marvel Cinematic Universe. Peter Parker''s world has been turned upside down as his identity has been revealed, and he finds himself on the run while seeking help from Doctor Strange.', '2022-12-16', 'https://image.tmdb.org/t/p/original//gbGHezV6yrhua0KfAgwrknSOiIY.jpg');
