INSERT INTO tv_show (title, overview, release_date, in_production, average_rating, number_of_episodes, number_of_seasons,status, poster_path) VALUES ('Breaking Bad', 'When Walter White, a New Mexico chemistry teacher, is diagnosed with Stage III cancer and given a prognosis of only two years left to live. He becomes filled with a sense of fearlessness and an unrelenting desire to secure his family''s financial future at any cost as he enters the dangerous world of drugs and crime.', '2008-01-20', 'false', '0', '10', '2', 'ongoing', 'https://image.tmdb.org/t/p/original//ggFHVNu6YYI5L9pCfOacjizRGt.jpg');
INSERT INTO tv_show (title, overview, release_date, in_production, average_rating, number_of_episodes, number_of_seasons,status, poster_path) VALUES ('Arcane', 'Amid the stark discord of twin cities Piltover and Zaun, two sisters fight on rival sides of a war between magic technologies and clashing convictions.', '2021-11-06', 'false', '0', '10', '2', 'ongoing', 'https://image.tmdb.org/t/p/original//fqldf2t8ztc9aiwn3k6mlX3tvRT.jpg');
INSERT INTO tv_show (title, overview, release_date, in_production, average_rating, number_of_episodes, number_of_seasons,status, poster_path) VALUES ('The Last of Us', 'Twenty years after modern civilization has been destroyed, Joel, a hardened survivor, is hired to smuggle Ellie, a 14-year-old girl, out of an oppressive quarantine zone. What starts as a small job soon becomes a brutal, heartbreaking journey, as they both must traverse the United States and depend on each other for survival.', '2023-01-15', 'false', '0', '10', '2', 'ongoing', 'https://image.tmdb.org/t/p/original//uKvVjHNqB5VmOrdxqAt2F7J78ED.jpg');
INSERT INTO tv_show (title, overview, release_date, in_production, average_rating, number_of_episodes, number_of_seasons,status, poster_path) VALUES ('Rick and Morty', 'Rick is a mentally-unbalanced but scientifically gifted old man who has recently reconnected with his family. He spends most of his time involving his young grandson Morty in dangerous, outlandish adventures throughout space and alternate universes. Compounded with Morty''s already unstable family life, these events cause Morty much distress at home and school.', '2013-12-02', 'false', '0', '10', '2', 'ongoing', 'https://image.tmdb.org/t/p/original//cvhNj9eoRBe5SxjCbQTkh05UP5K.jpg');
INSERT INTO tv_show (title, overview, release_date, in_production, average_rating, number_of_episodes, number_of_seasons,status, poster_path) VALUES ('One Piece', 'Years ago, the fearsome Pirate King, Gol D. Roger was executed leaving a huge pile of treasure and the famous "One Piece" behind. Whoever claims the "One Piece" will be named the new King of the Pirates.
INSERT INTO tv_show (title, overview, release_date, in_production, average_rating, number_of_episodes, number_of_seasons,status, poster_path) VALUES ('Fullmetal Alchemist: Brotherhood', 'Edward and Alphonse Elric''s reckless disregard for alchemy''s fun­damental laws ripped half of Ed''s limbs from his body and left Al''s soul clinging to a cold suit of armor. To restore what was lost, the brothers scour a war-torn land for the Philosopher''s Sto­ne, a fabled relic which grants the ability to perform alchemy in impossible ways.
INSERT INTO tv_show (title, overview, release_date, in_production, average_rating, number_of_episodes, number_of_seasons,status, poster_path) VALUES ('Demon Slayer: Kimetsu no Yaiba', 'It is the Taisho Period in Japan. Tanjiro, a kindhearted boy who sells charcoal for a living, finds his family slaughtered by a demon. To make matters worse, his younger sister Nezuko, the sole survivor, has been transformed into a demon herself. Though devastated by this grim reality, Tanjiro resolves to become a “demon slayer” so that he can turn his sister back into a human, and kill the demon that massacred his family.', '2019-04-06', 'false', '0', '10', '2', 'ongoing', 'https://image.tmdb.org/t/p/original//xUfRZu2mi8jH6SzQEJGP6tjBuYj.jpg');
INSERT INTO tv_show (title, overview, release_date, in_production, average_rating, number_of_episodes, number_of_seasons,status, poster_path) VALUES ('Anne with an E', 'A coming-of-age story about an outsider who, against all odds and numerous challenges, fights for love and acceptance and for her place in the world. The series centers on a young orphaned girl in the late 1890’s, who, after an abusive childhood spent in orphanages and the homes of strangers, is mistakenly sent to live with an elderly woman and her aging brother. Over time, 13-year-old Anne will transform their lives and eventually the small town in which they live with her unique spirit, fierce intellect and brilliant imagination.', '2017-03-19', 'false', '0', '10', '2', 'ongoing', 'https://image.tmdb.org/t/p/original//6P6tXhjT5tK3qOXzxF9OMLlG7iz.jpg');
INSERT INTO tv_show (title, overview, release_date, in_production, average_rating, number_of_episodes, number_of_seasons,status, poster_path) VALUES ('Goblin', 'In his quest for a bride to break his immortal curse, a 939-year-old guardian of souls meets a grim reaper and a sprightly student with a tragic past.', '2016-12-02', 'false', '0', '10', '2', 'ongoing', 'https://image.tmdb.org/t/p/original//t7aUi8jbsIUSCNqA1akAbKjBWjU.jpg');
INSERT INTO tv_show (title, overview, release_date, in_production, average_rating, number_of_episodes, number_of_seasons,status, poster_path) VALUES ('My Hero Academia', 'In a world where eighty percent of the population has some kind of super-powered Quirk, Izuku was unlucky enough to be born completely normal. But that won''t stop him from enrolling in a prestigious hero academy. Now, he''ll get his first taste of brutal rivalry from other schools as he braves the cutthroat, no-holds-barred provisional license exam.', '2016-04-03', 'false', '0', '10', '2', 'ongoing', 'https://image.tmdb.org/t/p/original//ivOLM47yJt90P19RH1NvJrAJz9F.jpg');
INSERT INTO tv_show (title, overview, release_date, in_production, average_rating, number_of_episodes, number_of_seasons,status, poster_path) VALUES ('Invincible', 'Mark Grayson is a normal teenager except for the fact that his father is the most powerful superhero on the planet. Shortly after his seventeenth birthday, Mark begins to develop powers of his own and enters into his father’s tutelage.', '2021-03-25', 'false', '0', '10', '2', 'ongoing', 'https://image.tmdb.org/t/p/original//yDWJYRAwMNKbIYT8ZB33qy84uzO.jpg');
INSERT INTO tv_show (title, overview, release_date, in_production, average_rating, number_of_episodes, number_of_seasons,status, poster_path) VALUES ('The Owl House', 'An animated fantasy-comedy series that follows Luz, a self-assured teenage girl who accidentally stumbles upon a portal to a magical world where she befriends a rebellious witch, Eda, and an adorably tiny warrior, King. Despite not having magical abilities, Luz pursues her dream of becoming a witch by serving as Eda''s apprentice at the Owl House and ultimately finds a new family in an unlikely setting.', '2020-01-10', 'false', '0', '10', '2', 'ongoing', 'https://image.tmdb.org/t/p/original//zhdy3PcNVE15wj1wrxn45ARZBnx.jpg');
INSERT INTO tv_show (title, overview, release_date, in_production, average_rating, number_of_episodes, number_of_seasons,status, poster_path) VALUES ('Avatar: The Last Airbender', 'In a war-torn world of elemental magic, a young boy reawakens to undertake a dangerous mystic quest to fulfill his destiny as the Avatar, and bring peace to the world.', '2005-02-21', 'false', '0', '10', '2', 'ongoing', 'https://image.tmdb.org/t/p/original//uXccE6tjaYyVflI3BEmZ7xHKPOs.jpg');
INSERT INTO tv_show (title, overview, release_date, in_production, average_rating, number_of_episodes, number_of_seasons,status, poster_path) VALUES ('Regular Show', 'Two bored groundskeepers, Mordecai (a six-foot-tall blue jay) and Rigby (a hyperactive raccoon) are best friends who spend their days trying to entertain themselves by any means necessary, much to the displeasure of their boss. Their everyday pursuits often lead to things spiraling out of control and into the surreal.', '2010-09-06', 'false', '0', '10', '2', 'ongoing', 'https://image.tmdb.org/t/p/original//mS5SLxMYcKfUxA0utBSR5MOAWWr.jpg');
INSERT INTO tv_show (title, overview, release_date, in_production, average_rating, number_of_episodes, number_of_seasons,status, poster_path) VALUES ('Kaguya-sama: Love Is War', 'Considered a genius due to having the highest grades in the country, Miyuki Shirogane leads the prestigious Shuchiin Academy''s student council as its president, working alongside the beautiful and wealthy vice president Kaguya Shinomiya. The two are often regarded as the perfect couple by students despite them not being in any sort of romantic relationship.', '2019-01-12', 'false', '0', '10', '2', 'ongoing', 'https://image.tmdb.org/t/p/original//5khbC6AuNgnvnoDbjIMKCOhEtIc.jpg');
INSERT INTO tv_show (title, overview, release_date, in_production, average_rating, number_of_episodes, number_of_seasons,status, poster_path) VALUES ('Alchemy of Souls', 'A powerful sorceress in a blind woman''s body encounters a man from a prestigious family, who wants her help to change his destiny.', '2022-06-18', 'false', '0', '10', '2', 'ongoing', 'https://image.tmdb.org/t/p/original//q2IiPRSXPOZ6qVRj36WRAYEQyHs.jpg');
INSERT INTO tv_show (title, overview, release_date, in_production, average_rating, number_of_episodes, number_of_seasons,status, poster_path) VALUES ('Chainsaw Man', 'Denji has a simple dream—to live a happy and peaceful life, spending time with a girl he likes. This is a far cry from reality, however, as Denji is forced by the yakuza into killing devils in order to pay off his crushing debts. Using his pet devil Pochita as a weapon, he is ready to do anything for a bit of cash.', '2022-10-12', 'false', '0', '10', '2', 'ongoing', 'https://image.tmdb.org/t/p/original//npdB6eFzizki0WaZ1OvKcJrWe97.jpg');
INSERT INTO tv_show (title, overview, release_date, in_production, average_rating, number_of_episodes, number_of_seasons,status, poster_path) VALUES ('Primal', 'A caveman forms a bond with a dinosaur as they struggle to survive in a hostile world.', '2019-10-08', 'false', '0', '10', '2', 'ongoing', 'https://image.tmdb.org/t/p/original//jflPzxYbM4MxxCppo2AbvSbTOLm.jpg');
INSERT INTO tv_show (title, overview, release_date, in_production, average_rating, number_of_episodes, number_of_seasons,status, poster_path) VALUES ('Hunter x Hunter', 'Twelve-year-old Gon Freecss one day discovers that the father he had always been told was dead was alive. His Father, Ging, is a Hunter — a member of society''s elite with a license to go anywhere or do almost anything. Gon, determined to follow in his father''s footsteps, decides to take the Hunter Examination and eventually find his father to prove himself as a Hunter in his own right. But on the way, he learns that there is more to becoming a Hunter than previously thought, and the challenges that he must face are considered the toughest in the world.', '2011-10-02', 'false', '0', '10', '2', 'ongoing', 'https://image.tmdb.org/t/p/original//ucpgmUR1h5Te1BYegKItoPjOeF7.jpg');
INSERT INTO tv_show (title, overview, release_date, in_production, average_rating, number_of_episodes, number_of_seasons,status, poster_path) VALUES ('Be Eternal: Champions of America', 'This series takes an unprecedented closeup look at Lionel Messi and the Argentine team''s battle to break a 28-year title drought in 2021''s Copa América.', '2022-11-03', 'false', '0', '10', '2', 'ongoing', 'https://image.tmdb.org/t/p/original//kBlqr507IV9bDKHLEFBtEtTfCw3.jpg');
INSERT INTO tv_show (title, overview, release_date, in_production, average_rating, number_of_episodes, number_of_seasons,status, poster_path) VALUES ('The Kardashians', 'The family you know and love is here with a brand new series, giving an all-access pass into their lives. Kris, Kourtney, Kim, Khloé, Kendall, and Kylie bring the cameras back to reveal the truth behind the headlines. From the intense pressures of running billion-dollar businesses to the hilarious joys of playtime and school drop-offs, this series brings viewers into the fold with a rivetingly honest story of love & life in the spotlight.', '2022-04-14', 'false', '0', '10', '2', 'ongoing', 'https://image.tmdb.org/t/p/original//3cJgB4fOfgHKNoHgtT3h1Qqkvxq.jpg');
INSERT INTO tv_show (title, overview, release_date, in_production, average_rating, number_of_episodes, number_of_seasons,status, poster_path) VALUES ('DARLING in the FRANXX', 'The story is set in the distant future. The land is ruined, and humanity establishes the mobile fort city Plantation. Pilots produced inside Plantation live in Mistilteinn, also know as the "birdcage." Children live there knowing nothing of the outside world or the freedom of the sky. Their lives consist of battling to carry out missions. Their enemies are mysterious giant lifeforms known as Kyouryuu, and the children pilot robots called Franxx to face off against them. For the children, riding the Franxx proves their existence.
INSERT INTO tv_show (title, overview, release_date, in_production, average_rating, number_of_episodes, number_of_seasons,status, poster_path) VALUES ('Sym-Bionic Titan', 'Sym-Bionic Titan is an American animated action science fiction television series created by Genndy Tartakovsky, Paul Rudish, and Bryan Andrews for Cartoon Network. The series focuses on a trio made up of the alien princess Ilana, the rebellious soldier Lance, and the robot Octus; the three are able to combine to create the titular Sym-Bionic Titan. A preview of the series was first shown at the 2009 San Diego Comic-Con International, and further details were revealed at Cartoon Network''s 2010 Upfront. The series premiered on September 17, 2010, and ended on April 9, 2011. The series is rated TV-PG-V.
INSERT INTO tv_show (title, overview, release_date, in_production, average_rating, number_of_episodes, number_of_seasons,status, poster_path) VALUES ('Once', 'The series revolves around Gabo, a soccer-loving teenager who, upon receiving a scholarship from the prestigious Sports Academic Institute (IAD) of Buenos Aires, will see his dream of playing at Los Halcones Dorados, the renowned amateur team of the school, and also his longing to become a professional footballer.', '2017-06-19', 'false', '0', '10', '2', 'ongoing', 'https://image.tmdb.org/t/p/original//sen4We5WQCRsGZe82ohTEmxFD06.jpg');
INSERT INTO tv_show (title, overview, release_date, in_production, average_rating, number_of_episodes, number_of_seasons,status, poster_path) VALUES ('Young Royals', 'Prince Wilhelm adjusts to life at his prestigious new boarding school, Hillerska, but following his heart proves more challenging than anticipated.', '2021-07-01', 'false', '0', '10', '2', 'ongoing', 'https://image.tmdb.org/t/p/original//8o3agKlXNOIjN2SdCdbjLH48GKk.jpg');
INSERT INTO tv_show (title, overview, release_date, in_production, average_rating, number_of_episodes, number_of_seasons,status, poster_path) VALUES ('Fighting Spirit', 'Makunouchi Ippo is an ordinary high school student in Japan. Since he spends most of his time away from school helping his mother run the family business, he doesn''t get to enjoy his younger years like most teenagers. Always a target for bullying at school (the family fishing business grants him a distinct odor), Ippo''s life is one of hardship. One of these after-school bullying sessions turns Ippo''s life around for the better, as he is saved by a boxer named Takamura. He decides to follow in Takamura''s footsteps and train to become a boxer, giving his life direction and purpose. Ippo''s path to perfecting his pugilistic prowess is just beginning...', '2000-10-03', 'false', '0', '10', '2', 'ongoing', 'https://image.tmdb.org/t/p/original//i3U3J2MWovIBZBnZYYiOLBXqNJZ.jpg');
INSERT INTO tv_show (title, overview, release_date, in_production, average_rating, number_of_episodes, number_of_seasons,status, poster_path) VALUES ('Attack on Titan', 'Several hundred years ago, humans were nearly exterminated by Titans. Titans are typically several stories tall, seem to have no intelligence, devour human beings and, worst of all, seem to do it for the pleasure rather than as a food source. A small percentage of humanity survived by walling themselves in a city protected by extremely high walls, even taller than the biggest Titans. Flash forward to the present and the city has not seen a Titan in over 100 years. Teenage boy Eren and his foster sister Mikasa witness something horrific as the city walls are destroyed by a Colossal Titan that appears out of thin air. As the smaller Titans flood the city, the two kids watch in horror as their mother is eaten alive. Eren vows that he will murder every single Titan and take revenge for all of mankind.', '2013-04-07', 'false', '0', '10', '2', 'ongoing', 'https://image.tmdb.org/t/p/original//sHim6U0ANsbzxcmNRYuIubBVQaz.jpg');
INSERT INTO tv_show (title, overview, release_date, in_production, average_rating, number_of_episodes, number_of_seasons,status, poster_path) VALUES ('SPY x FAMILY', 'World peace is at stake and secret agent Twilight must undergo his most difficult mission yet—pretend to be a family man. Posing as a loving husband and father, he’ll infiltrate an elite school to get close to a high-profile politician. He has the perfect cover, except his wife’s a deadly assassin and neither knows each other’s identity. But someone does, his adopted daughter who’s a telepath!', '2022-04-09', 'false', '0', '10', '2', 'ongoing', 'https://image.tmdb.org/t/p/original//3r4LYFuXrg3G8fepysr4xSLWnQL.jpg');
INSERT INTO tv_show (title, overview, release_date, in_production, average_rating, number_of_episodes, number_of_seasons,status, poster_path) VALUES ('Neon Genesis Evangelion', 'At the turn of the century, the Angels returned to Earth, seeking to wipe out humanity in an apocalyptic fury. Devastated, mankind''s last remnants moved underground to wait for the day when the Angels would come back to finish the job. Fifteen years later, that day has come... but this time, humanity is ready to fight back with terrifying bio-mechanical weapons known as the Evangelions. Watch as Shinji, Rei, Asuka and the rest of the mysterious shadow agency Nerv battle to save earth from total annihilation.', '1995-10-04', 'false', '0', '10', '2', 'ongoing', 'https://image.tmdb.org/t/p/original//y2ah9t0navXyIvoHg1uIbIHO3tt.jpg');
INSERT INTO tv_show (title, overview, release_date, in_production, average_rating, number_of_episodes, number_of_seasons,status, poster_path) VALUES ('KONOSUBA – God''s blessing on this wonderful world!!', 'After a traffic accident, Kazuma Sato’s  disappointingly brief life was supposed to be over, but he wakes up to  see a beautiful girl before him. She claims to be a goddess, Aqua, and  asks if he would like to go to another world and bring only one thing  with him. Kazuma decides to bring the goddess herself, and they are  transported to a fantasy world filled with adventure, ruled by a demon  king. Now Kazuma only wants to live in peace, but Aqua wants to solve  many of this world’s problems, and the demon king will only turn a blind  eye for so long…', '2016-01-14', 'false', '0', '10', '2', 'ongoing', 'https://image.tmdb.org/t/p/original//oRaOeQlwktbGSd2T31FYAcgHZlh.jpg');
INSERT INTO tv_show (title, overview, release_date, in_production, average_rating, number_of_episodes, number_of_seasons,status, poster_path) VALUES ('Big Time Rush', 'Four teenage friends move from Minneapolis to Los Angeles to form a potential chart-topping boy band after Kendall is inadvertently discovered by an eccentric record executive, Gustavo Rocque. As they seize this opportunity of a lifetime, these friends embark on an exciting comedy and music-filled journey to prove to themselves and their record label that they are serious about their new career choice.', '2009-11-28', 'false', '0', '10', '2', 'ongoing', 'https://image.tmdb.org/t/p/original//5yzb0iWXilLpg3iz1LT3H3UGBYs.jpg');
INSERT INTO tv_show (title, overview, release_date, in_production, average_rating, number_of_episodes, number_of_seasons,status, poster_path) VALUES ('Asobi Asobase - workshop of fun -', 'During recess, Olivia, a foreign transfer student who doesn''t know English, plays a game of "look-the-other-way" with Hanako Honda, a loud-mouthed airhead. Their rowdy behavior spurs the ire of Kasumi Nomura, a deadpan loner constantly teased by her older sister for her tendency to lose games. Not willing to compete, Kasumi declines Olivia''s offer to join the fun, but eventually gets involved anyway and dispenses her own brand of mischief. Soon, a strange friendship blossoms between the peculiar trio, and they decide to form the "Pastime Club," where they are free to resume their daily hijinks.
INSERT INTO tv_show (title, overview, release_date, in_production, average_rating, number_of_episodes, number_of_seasons,status, poster_path) VALUES ('High School D×D', 'The story follows Issei Hyōdō, a dim-witted, lecherous second-year high school student who is killed by a girl on his first date ever. Issei is reincarnated as a devil, and from that day forward, he serves as an underling of Riasu, a high-level devil who is also the prettiest girl on Issei''s campus.', '2012-01-06', 'false', '0', '10', '2', 'ongoing', 'https://image.tmdb.org/t/p/original//nuGXXJMsinqGAva3niLI00x1h3K.jpg');
INSERT INTO tv_show (title, overview, release_date, in_production, average_rating, number_of_episodes, number_of_seasons,status, poster_path) VALUES ('My Dress-Up Darling', 'Wakana Gojo is a high school boy who wants to become a kashirashi--a master craftsman who makes traditional Japanese Hina dolls. Though he''s gung-ho about the craft, he knows nothing about the latest trends, and has a hard time fitting in with his class. The popular kids--especially one girl, Marin Kitagawa--seem like they live in a completely different world. That all changes one day, when she shares an unexpected secret with him, and their completely different worlds collide.', '2022-01-09', 'false', '0', '10', '2', 'ongoing', 'https://image.tmdb.org/t/p/original//A6mxBwvvv63JXZm3xXKv4SugE0L.jpg');
INSERT INTO tv_show (title, overview, release_date, in_production, average_rating, number_of_episodes, number_of_seasons,status, poster_path) VALUES ('I Am Not an Animal', 'I Am Not An Animal is an animated comedy series about the only six talking animals in the world, whose cosseted existence in a vivisection unit is turned upside down when they are liberated by animal rights activists.', '2004-05-10', 'false', '0', '10', '2', 'ongoing', 'https://image.tmdb.org/t/p/original//qG59J1Q7rpBc1dvku4azbzcqo8h.jpg');
INSERT INTO tv_show (title, overview, release_date, in_production, average_rating, number_of_episodes, number_of_seasons,status, poster_path) VALUES ('Rascal Does Not Dream of Bunny Girl Senpai', 'Puberty Syndrome—a rumored, mysterious syndrome that only affects those in their puberty. For example, a bunny girl suddenly appeared in front of Sakuta Azusagawa. The bunny girl''s real identity is Mai Sakurajima, a teenage celebrity who is currently an inactive high school senior. For some reason, her charming figure does not reflect in the eyes of others. In the course of revealing the mystery behind this phenomenon, Sakuta begins to explore his feelings towards Mai. Set in a city where the skies and seas shine, Sakuta unfolds the meaning behind his bizarre encounters on women with the said syndrome.', '2018-10-04', 'false', '0', '10', '2', 'ongoing', 'https://image.tmdb.org/t/p/original//cU4jHfo1Q9AEPnFqFqtuSA74gdi.jpg');
INSERT INTO tv_show (title, overview, release_date, in_production, average_rating, number_of_episodes, number_of_seasons,status, poster_path) VALUES ('Lookism', 'In a society that favors good looks, a high school outcast leads a double life switching between his two bodies that are polar opposites in appearance.', '2022-12-08', 'false', '0', '10', '2', 'ongoing', 'https://image.tmdb.org/t/p/original//qkoM63HDuCOSwxGfb0pljrgns9I.jpg');
INSERT INTO tv_show (title, overview, release_date, in_production, average_rating, number_of_episodes, number_of_seasons,status, poster_path) VALUES ('JoJo''s Bizarre Adventure', 'Follow the intergenerational feud between the Joestar Family and various forces of evil, the most prominent of which is Dio Brando and his followers.', '2012-10-06', 'false', '0', '10', '2', 'ongoing', 'https://image.tmdb.org/t/p/original//ogAWwbh3frWtiTyyXrZaVFtqCgp.jpg');
INSERT INTO tv_show (title, overview, release_date, in_production, average_rating, number_of_episodes, number_of_seasons,status, poster_path) VALUES ('Death Note', 'Light Yagami is an ace student with great prospects—and he’s bored out of his mind. But all that changes when he finds the Death Note, a notebook dropped by a rogue Shinigami death god. Any human whose name is written in the notebook dies, and Light has vowed to use the power of the Death Note to rid the world of evil. But will Light succeed in his noble goal, or will the Death Note turn him into the very thing he fights against?', '2006-10-04', 'false', '0', '10', '2', 'ongoing', 'https://image.tmdb.org/t/p/original//iigTJJskR1PcjjXqxdyJwVB3BoU.jpg');
INSERT INTO tv_show (title, overview, release_date, in_production, average_rating, number_of_episodes, number_of_seasons,status, poster_path) VALUES ('Haikyu!!', 'Based off of the original Weekly Shonen Jump manga series from Haruichi Furudate, Haikyu!! is a slice-of-life sports anime revolving around Shouyou Hinata''s love of volleyball. Inspired by a small-statured pro volleyball player, Hinata creates a volleyball team in his last year of middle school. Unfortunately the team is matched up against the "King of the Court" Tobio Kageyama''s team in their first tournament and inevitably lose. After the crushing defeat, Hinata vows to surpass Kageyama. After entering high school, Hinata joins the volleyball team only to find that Tobio has also joined.', '2014-04-06', 'false', '0', '10', '2', 'ongoing', 'https://image.tmdb.org/t/p/original//ecQ84z2sR0XapZu435MnFuEBzD8.jpg');
